                    sum += checkArea(rowNumber - 1, colNumber, paperGrid.size, row.length, paperGrid)
                    sum += checkArea(rowNumber - 1, colNumber + 1, paperGrid.size, row.length, paperGrid)
                    sum += checkArea(rowNumber - 1, colNumber - 1, paperGrid.size, row.length, paperGrid)
                    sum += checkArea(rowNumber + 1, colNumber, paperGrid.size, row.length, paperGrid)
                    sum += checkArea(rowNumber + 1, colNumber + 1, paperGrid.size, row.length, paperGrid)
                    sum += checkArea(rowNumber + 1, colNumber - 1, paperGrid.size, row.length, paperGrid)

                    if (sum < 4) {
                        val charArray = paperGrid[rowNumber].toCharArray()
                        charArray[colNumber] = 'x'
                        paperGrid[rowNumber] = String(charArray)
                        rolls++
                    }
                }
            }
        }
        paperGrid.forEachIndexed { rowNumber, row ->
            paperGrid[rowNumber] = paperGrid[rowNumber].replace('x','.')
        }
        total += rolls
        if(rolls == 0) break
    }
    println("Part 2 Sum: $total")
}
private fun checkArea(x: Int, y: Int, gridSize: Int, rowLength: Int, grid: List<String>): Int {
    if(x < 0||y < 0||x >= gridSize||y >= rowLength) return 0
    if(grid[x][y] == '@') return 1
    return 0
}